import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [sponsorData, setSponsorData] = useState({
    sponsor_name: '',
    sponsor_phone: '',
    farmer_name: '',
    amount: '',
    crop: '',
    quantity: ''
  });

  const handleChange = (e) => {
    setSponsorData({ ...sponsorData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://127.0.0.1:8000/api/sponsorship/sponsor/', sponsorData);
      console.log('Success:', response.data);
      alert('Sponsorship created!');
    } catch (error) {
      console.error('Error:', error.response.data);
      alert('Failed to create sponsorship.');
    }
  };

  return (
    <div className="App">
      <h1>AgriTech Prototype</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="sponsor_name"
          placeholder="Sponsor Name"
          value={sponsorData.sponsor_name}
          onChange={handleChange}
        />
        <input
          type="text"
          name="sponsor_phone"
          placeholder="Sponsor Phone"
          value={sponsorData.sponsor_phone}
          onChange={handleChange}
        />
        <input
          type="text"
          name="farmer_name"
          placeholder="Farmer Name"
          value={sponsorData.farmer_name}
          onChange={handleChange}
        />
        <input
          type="number"
          name="amount"
          placeholder="Amount"
          value={sponsorData.amount}
          onChange={handleChange}
        />
        <input
          type="text"
          name="crop"
          placeholder="Crop"
          value={sponsorData.crop}
          onChange={handleChange}
        />
        <input
          type="number"
          name="quantity"
          placeholder="Quantity"
          value={sponsorData.quantity}
          onChange={handleChange}
        />
        <button type="submit">Create Sponsorship</button>
      </form>
    </div>
  );
}

export default App;
